const db = firebase.firestore();

const container = document.getElementById("container");
const headTable = document.getElementById("headTable");
const h3 = document.getElementById("h3");
const sampleWieght = document.getElementById("sample");
const overlay = document.getElementById("overlay");
const icon = document.getElementById("icon");
const link = document.getElementById("link");
const nav = document.getElementById("nav");
const excel = document.getElementById("excel");
const body = document.body;
let receivedList = JSON.parse(localStorage.getItem("element"));
let email = localStorage.getItem("email");
var dataList = [];
let content;
let saveButton;

icon.addEventListener("click", function () {
  if (link.style.display == "flex") {
    link.style.display = "none";
  } else {
    link.style.display = "flex";
  }
});

link.addEventListener("click", (event) => {
  if (event.target === link) {
    link.style.display = "none";
  }
});

body.addEventListener("click", (event) => {
  if (
    event.target === body ||
    event.target === container ||
    event.target === content
  ) {
    link.style.display = "none";
  }
});

excel.addEventListener("click", async function () {
  const workbook = new ExcelJS.Workbook();
  const sheet = workbook.addWorksheet("Sheet1");

  sheet.columns = [{ width: 12 }, { width: 12 }, { width: 15 }, { width: 15 }];

  // الصفوف الفاضية لحد الصف 4
  for (let i = 1; i <= 4; i++) {
    sheet.addRow([]);
  }

  // إضافة رقم ووزن العينة في الصف الخامس
  sheet.addRow([5000, "", "", "وزن العينة"]);
  sheet.mergeCells("A5:C5");

  // دمج وإضافة الصورة (Base64)
  const imageId = workbook.addImage({
    base64:
      "data:image/jpeg;base64,",
    extension: "png",
  });
  sheet.addImage(imageId, "A1:B4");

  // الصف السادس والسابع للعناوين
  sheet.addRow(["sieve", "", "resrved", "passing"]);
  sheet.addRow(["no", "mm", "resrved", "passing"]);

  // إضافة البيانات
  dataList.forEach((o) => {
    sheet.addRow([o.no ?? "", o.mm ?? "", o.resrved ?? "", o.passing ?? ""]);
  });

  // دمج الأعمدة الرئيسية
  sheet.mergeCells("A6:B6");
  sheet.mergeCells("C6:C7");
  sheet.mergeCells("D6:D7");

  // تنسيقات العناوين
  [
    "A5",
    "B5",
    "C5",
    "D5",
    "A6",
    "B6",
    "C6",
    "D6",
    "A7",
    "B7",
    "C7",
    "D7",
  ].forEach((cell) => {
    const c = sheet.getCell(cell);
    c.alignment = { vertical: "middle", horizontal: "center" };
    c.font = { bold: true };
    c.fill = {
      type: "pattern",
      pattern: "solid",
      fgColor: { argb: "FFD9D9D9" },
    };
    c.border = {
      top: { style: "thin" },
      left: { style: "thin" },
      bottom: { style: "thin" },
      right: { style: "thin" },
    };
  });

  // تنسيقات باقي الجدول
  const lastRow = sheet.lastRow.number;
  for (let r = 8; r <= lastRow; r++) {
    for (let c = 1; c <= 4; c++) {
      const cell = sheet.getCell(r, c);
      cell.alignment = { vertical: "middle", horizontal: "center" };
      cell.border = {
        top: { style: "thin" },
        left: { style: "thin" },
        bottom: { style: "thin" },
        right: { style: "thin" },
      };
    }
  }

  // حفظ الملف وفتحه
  const buffer = await workbook.xlsx.writeBuffer();
  const blob = new Blob([buffer], {
    type: "application/vnd.openxmlformats-officedocument.spreadsheetml.sheet",
  });
  const url = URL.createObjectURL(blob);
  window.open(url, "_blank");
});

function start() {
  h3.innerHTML = receivedList["date"];
  h3.style.display = "flex";
  headTable.style.display = "flex";
  container.style.display = "flex";

  content = globalSieve();
  if (email == receivedList["email"]) {
    sampleWieght.disabled = false;
  } else {
    sampleWieght.disabled = true;
  }

  sampleWieght.value = receivedList["weight"];

  fetchData(content);

  saveButton = document.createElement("div");
  saveButton.classList.add("btn");
  saveButton.innerHTML = "تعديل";

  saveButton.addEventListener("click", function () {
    const sampleWeightInput = document.getElementById("sample");
    const sampleWeight = sampleWeightInput.value.trim();
    //=========
    dialog.innerHTML = `
        <div class="loader"></div>
        <p>جاري التعديل...</p>
      `;
    overlay.style.display = "flex";
    //=============
    db.collection("test")
      .where("selectedText", "==", receivedList.selectedText)
      .where("date", "==", receivedList.date)
      .where("sampleNo", "==", receivedList.sampleNo)
      .get()
      .then((querySnapshot) => {
        querySnapshot.forEach((doc) => {
          doc.ref
            .update({
              weight: sampleWeight,
              list: dataList,
            })
            .then(() => {
              overlay.style.display = "none";
              console.log("تم الحفظ بنجاح");
            })
            .catch((error) => {
              overlay.style.display = "none";
              console.error("حصل خطأ أثناء الحفظ:", error);
            });
        });
      })
      .catch((error) => {
        console.error("❌ خطأ:", error);
        overlay.style.display = "none";
      });
  });

  container.appendChild(content);
  if (email == receivedList["email"]) {
    container.appendChild(saveButton);
  }
}

function rowTable(sieveMM, sieveNo, pass, resrved, index) {
  const rowTable = document.createElement("div");
  rowTable.classList.add("rowTable");
  rowTable.addEventListener("click", (event) => {
    if (event.target === rowTable) {
      link.style.display = "none";
      // nav.style.backgroundColor = "#aaef58";
    }
  });
  const titleSieve = document.createElement("titleSieve");
  titleSieve.classList.add("titleSieve");
  const mm = document.createElement("div");
  const no = document.createElement("div");
  mm.innerHTML = sieveMM;
  no.innerHTML = sieveNo;
  titleSieve.appendChild(mm);
  titleSieve.appendChild(no);
  const input = document.createElement("input");
  input.classList.add("resrved");
  input.value = resrved;
  if (email == receivedList["email"]) {
    input.disabled = false;
  } else {
    input.disabled = true;
  }

  const passing = document.createElement("div");
  passing.classList.add("passing");
  passing.innerHTML = pass;

  input.addEventListener("input", function () {
    passing.innerHTML = getPassingForOneSieve(input.value);
    dataList[index].resrved = input.value;
    dataList[index].passing = getPassingForOneSieve(input.value);
  });
  //============
  sampleWieght.addEventListener("input", function () {
    if (sampleWieght.value != "") {
      passing.innerHTML = "";
      if (input.value != "") {
        passing.innerHTML = getPassingForOneSieve(input.value);
        dataList[index].resrved = input.value;
        dataList[index].passing = getPassingForOneSieve(input.value);
      }
    } else {
      passing.innerHTML = "";
    }
  });
  //===========
  rowTable.appendChild(titleSieve);
  rowTable.appendChild(input);
  rowTable.appendChild(passing);

  return rowTable;
}

function globalSieve() {
  const content = document.createElement("div");
  content.classList.add("contnetTable");
  return content;
}

function fetchData(content) {
  var weight = 0;
  db.collection("test")
    .where("selectedText", "==", receivedList["selectedText"])
    .where("date", "==", receivedList["date"])
    .where("sampleNo", "==", receivedList["sampleNo"])
    .get()
    .then((querySnapshot) => {
      if (!querySnapshot.empty) {
        querySnapshot.forEach((doc) => {
          dataList = doc.data()["list"];
          weight = doc.data()["weight"];
        });
        sampleWieght.value = weight;
        dataList.forEach((element, index) => {
          const hr = document.createElement("hr");
          content.appendChild(
            rowTable(
              element["mm"],
              element["no"],
              element["passing"],
              element["resrved"],
              index
            )
          );
          if (index < dataList.length - 1) {
            content.appendChild(hr);
          }
        });
      } else {
        console.log("لا توجد بيانات");
      }
    })
    .catch((error) => {
      console.error("خطأ في جلب البيانات:", error);
    });
}

function getPassingForOneSieve(resrved) {
  const value = sampleWieght.value.trim();
  if (value !== "" && !isNaN(value)) {
    var passing = Number(value) - Number(resrved);
    var percentPass = ((passing / value) * 100).toFixed(2);
    return `${percentPass} %`;
  }
}

start();
